template:
{% macro card(card) %}
<div class="card" style="width: 400px">
  {% if card.image %}
    <img src="{{ card.image.src  }}" class="card-img-top" alt="{{ card.image.alt  }}">
  {% endif %}

  <div class="card-body">
  {% if card.title %}
    <h5 class="card-title">{{ card.title }}</h5>
  {% endif %}

  {% if card.text %}
    <p class="card-text">{{ card.text | safe }}</p>
  {% endif %}

  {{  caller() }}
  </div>
</div>
{% endmacro %}

func:
function myFilter(value) {
    return `This is myFilter: ${value.toUpperCase()}!!!`
}

global.get('nj.addFilter')('myFilter', myFilter)
